;(function(){"use strict";var app=document.querySelector("#app");var duration=120;var data={timer:duration,done:false};var countdown;function template(){var minutes=Math.floor(data.timer/60);var seconds=data.timer%60;if(data.done){return"‚è∞ <p><button id='restart'>Restart Timer</button></p>"}return minutes+":"+seconds.toString().padStart(2,"0")}function render(){if(app.innerHTML===template())return;app.innerHTML=template()}function update(){var time=data.timer-1;var done=time===0?true:false;data.timer=time;data.done=done;render();if(done){window.clearInterval(countdown)}}function start(){data.timer=duration;data.done=false;render();countdown=window.setInterval(update,1e3)}start();document.body.addEventListener("click",(function(event){if(event.target.id==="restart"){start()}}),false)})();
