;(function(){"use strict";var app=document.querySelector("#app");var duration=60;var data={timer:duration,done:false};var countdown;function template(){if(data.done){return"‚è∞ <p><button id='restart'>Restart Timer</button></p>"}return data.timer}function render(){if(app.innerHTML===template())return;app.innerHTML=template()}function start(){data.timer=duration;data.done=false;render();countdown=window.setInterval((function(){var time=data.timer-1;var done=time===0?true:false;data.timer=time;data.done=done;render();if(done){window.clearInterval(countdown)}}),1e3)}start();document.body.addEventListener("click",(function(event){if(event.target.id==="restart"){start()}}),false)})();
