;(function(){"use strict";var app=document.querySelector("#app");var duration=120;var data={timer:duration,paused:true,done:false};var countdown;function setData(object){for(var key in object){if(object.hasOwnProperty(key)){data[key]=object[key]}}render()}function getData(){return JSON.parse(JSON.stringify(data))}function template(){var minutes=Math.floor(data.timer/60);var seconds=data.timer%60;var string;string="<p>"+minutes+":"+seconds.toString().padStart(2,"0")+"</p>";string+="<p class='di'>"+"<button class='ma1 f3' type='button'"+(data.done?" disabled":"")+">"+(data.paused?"Start":"Pause")+"</button>"+"</p>";string+="<p class='di'><button class='ma1 f3' type='button'>Reset</button></p>";return string}function render(){if(app.innerHTML===template())return;app.innerHTML=template()}function update(){var dataCopy=getData();var time=dataCopy.timer-1;var done=time===0?true:false;dataCopy.timer=time;dataCopy.done=done;setData(dataCopy);if(done){window.clearInterval(countdown)}}function start(event){if(event.target.textContent!=="Start")return;var dataCopy=getData();dataCopy.paused=false;setData(dataCopy);countdown=window.setInterval(update,1e3)}function pause(event){if(event.target.textContent!=="Pause")return;var dataCopy=getData();dataCopy.paused=true;setData(dataCopy);window.clearInterval(countdown)}function reset(event){if(event.target.textContent!=="Reset")return;var dataCopy=getData();dataCopy.timer=duration;dataCopy.paused=true;dataCopy.done=false;setData(dataCopy);window.clearInterval(countdown)}render();app.addEventListener("click",(function(event){start(event);pause(event);reset(event)}),false)})();
