;(function(){"use strict";var app=document.querySelector("#app");var duration=120;var data={timer:duration,paused:true,done:false};var countdown;function template(){var minutes=Math.floor(data.timer/60);var seconds=data.timer%60;var string;string="<p>"+minutes+":"+seconds.toString().padStart(2,"0")+"</p>";string+="<p class='di'>"+"<button class='ma1 f3' type='button'"+(data.done?" disabled":"")+">"+(data.paused?"Start":"Pause")+"</button>"+"</p>";string+="<p class='di'><button class='ma1 f3' type='button'>Reset</button></p>";return string}function render(){if(app.innerHTML===template())return;app.innerHTML=template()}function update(){var time=data.timer-1;var done=time===0?true:false;data.timer=time;data.done=done;render();if(done){window.clearInterval(countdown)}}function start(event){if(event.target.textContent!=="Start")return;data.paused=false;countdown=window.setInterval(update,1e3);render()}function pause(event){if(event.target.textContent!=="Pause")return;data.paused=true;window.clearInterval(countdown);render()}function reset(event){if(event.target.textContent!=="Reset")return;data.timer=duration;data.paused=true;data.done=false;render();window.clearInterval(countdown)}render();app.addEventListener("click",(function(event){start(event);pause(event);reset(event)}),false)})();
